mapper.insert:
insert into goods(GOODS_NUM , GOODS_NAME , GOODS_PRICE, GOODS_CONTENT, DELIVERY_COST, VISIT_COUNT
,goods_Main
,goods_images
,goods_Original
,goods_Main_Org
,EMP_NUM,GOODS_REGI_DATE,EMP_UPT_DATE,EMP_UPT_NUM) values(#{goodsNum},#{goodsName}, #{goodsPrice},#{goodsContent},#{deliveryCost},0
,#{goodsMain}
,#{goodsImages}
,#{goodsOriginal}
,#{goodsMainOrg}
,#{empNum}, sysdate, null, null)

mapper.update:
update goods
set VISIT_COUNT = VISIT_COUNT + 1
where GOODS_NUM = #{goodsNum}

update goods
GOODS_NAME = #{goodsName}, GOODS_PRICE = #{goodsPrice} , GOODS_CONTENT = #{goodsContent}, DELIVERY_COST = #{deliveryCost},
and goods_num = #{goodsNum}
