mapper.update:
update review
set review_content = #{reviewContent}
where review_num = #{reviewNum}

mapper.select:
select GOODS_NUM goodsNum, REVIEW_NUM reviewNum
from   review r
where  REVIEW_NUM = #{reviewNum}

select GOODS_NUM goodsNum, GOODS_NAME goodsName, GOODS_MAIN goodsMain
from goods
where  goods_num = #{goodsNum}
