mapper.update:
update cart
set CART_QTY = CART_QTY - 1
where MEMBER_NUM =  #{memberNum} and GOODS_NUM = #{goodsNum}

mapper.delete:
delete from cart
where MEMBER_NUM =  #{memberNum} 
and	GOODS_NUM in

	#{goodsNum}

mapper.select:
select  	
from goods
where goods_num in(
select goods_num from wish where member_num = #{memberNum}
)
